$(document).ready(function(){$("#enlarge").click(function(){$("#col1").toggleClass("col-sm-6 col-sm-12");$("#col2").toggleClass("col-sm-6 col-sm-12");$(".thumb").toggleClass("col-sm-3 col-sm-2")})});
function initialize(a){geocoder=new google.maps.Geocoder;geocoder.geocode({address:a},function(a,b){if(b==google.maps.GeocoderStatus.OK){var c={zoom:12,center:a[0].geometry.location,mapTypeId:google.maps.MapTypeId.ROADMAP};map=new google.maps.Map(document.getElementById("map-canvas"),c);new google.maps.Marker({map:map,position:a[0].geometry.location})}});google.maps.event.addDomListener(window,"load",initialize)}
function shareItem(){$("#share-error").stop().fadeOut("fast");$.when(shareItemAjax()).then(function(a){$("#share-success").stop().fadeOut("fast").fadeIn("fast")})}function shareItemAjax(){return $.ajax({url:"/vault/share/"+eventId,type:"post",dataType:"json",cache:!1,data:$("#share-form").serialize()})};
