function validateStep(a){clearErrors();switch(a){case 1:ValidateReferenceOrName($("#reference-code").val(),$("#firstname").val(),$("#lastname").val()),ValidateUserEmailAddress($("#emailaddress").val()),ValidateUserDateOfBirth($("#dob-formatted").val()),validateTelephoneNumber($("#phone").val())||errors.push("Please provide a valid telephone number"),validateTelephoneNumber($("#mobile").val())||errors.push("Please provide a valid mobile number")}return 0==errors.length}
function ValidateReferenceOrName(a,b,c){""!=a||""!=b&&""!=c||errors.push("You must provide either a Reference or a First Name and Last Name")}function ValidateUserEmailAddress(a){""==a||validateEmail(a)||errors.push("Please enter a valid email address")}
function ValidateUserDateOfBirth(a){""!=a&&(a=new moment(a,"DD-MM-YYYY"),a.isValid()?(18>moment().diff(a,"years")||120<moment().diff(a,"years"))&&errors.push("User must be aged 18 or over"):errors.push("Invalid date of birth entered (DD/MM/YYYY)"))};
