var errors=[];function displayErrors(b){b="undefined"!==typeof b?b:"#error-box";var a="";$(errors).each(function(b,c){a=a+c+"<br/>"});$(b).stop().fadeOut().html(a).fadeIn()}function displaySuccess(b,a){$("undefined"!==typeof a?a:"#success-box").stop().fadeOut().html(b).fadeIn()}function clearErrors(b,a){a="undefined"!==typeof a?a:"#error-box";errors=[];$("undefined"!==typeof b?b:"#success-box").fadeOut("fast");$(a).fadeOut("fast")};
